var map;
var gMapsLoaded = false;

window.initMap = function(){
    // Create temp div container for map.
    var mapCanvas = document.createElement('canvas');
    mapCanvas.id = "map_canvas";
    mapCanvas.width = 300;
    mapCanvas.height = 300;
    mapCanvas.style["background-color"] = "#faafbe";
    mapCanvas.style.width = "300px";
    mapCanvas.style.height = "300px";
    mapCanvas.style["max-width"] = "none";
    mapCanvas.style.zIndex = 8;
    mapCanvas.style.border = "1px solid";
    mapCanvas.style.position = "absolute";
    mapCanvas.style.right = "100px";
    mapCanvas.style.bottom = "100px";
    document.body.appendChild(mapCanvas);

    // Create map.
    map = new google.maps.Map(mapCanvas, {
        center: {lat: -34.397, lng: 150.644},
        zoom: 8,
        scrollwheel: false
    });
}

window.gMapsCallback = function(){
    gMapsLoaded = true;
    var event = new CustomEvent('gMapsLoaded', {bubbles: true, cancelable: true});
    window.dispatchEvent(event);
}

window.loadGoogleMaps = function(){
    if(gMapsLoaded) return window.gMapsCallback();
    var script_tag = document.createElement('script');
    script_tag.type = "text/javascript";
    script_tag.async = true;
    script_tag.defer = true;
    script_tag.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyBBaV7CJ-7dikbMmBkEBmGtWcVdCSKwPng&callback=gMapsCallback";
    (document.getElementsByTagName("head")[0] || document.documentElement).appendChild(script_tag);
}

