define({
    inputs: {
        positionX: "number",
        positionY: "number",
        resturantPositionX: "number",
        resturantPositionY: "number",
        scale: "number"
    },
    outputs: {
        distanceText: "string"
    },
    setup: function(inputs, outputs) {

    },
    run: function(inputs, outputs) {
        var lenXSqr = Math.abs((inputs.positionX - inputs.resturantPositionX) * 2);
        var lenYSqr = Math.abs((inputs.positionY - inputs.resturantPositionY) * 2);
        var distance = Math.sqrt(lenXSqr + lenYSqr) * inputs.scale;
        var time = (distance / 5000) * 60;
        outputs.distanceText = "" + time.toFixed() + " min";
    }
});

