});

define({
    inputs: {
        flag: "boolean",
        label: "string"
    },
    outputs: {
        flag: "boolean",
        label: "string",
    },
    setup: function(inputs, outputs) {
        outputs.label = '';
    },
    run: function(inputs, outputs) {
        outputs.flag = inputs.flag;
        if(outputs.flag == true) //input flag 1 : visible, 0 : invisible
            outputs.label = inputs.label;

        // Send event.
        Noodl.eventEmitter.emit("Filter Switch", {
            flag: outputs.flag,
            label: outputs.label
        });
    }
});
